let silentCount = 0

function MyComponent() {
  const [count, setCount] = useState()

  return (
    <>
      <div>{count}</div>
      <button onClick={() => {silentCount++; setCount(v => v++)}}>Increment</button>
      <button onClick={() => alert(silentCount)}>Show me silent count</button>
    <>
  )
}

